import {useState} from 'react'
import Header from './components/Header.js'
import Players from './components/Players.js'
import AddPlayer from './components/AddPlayer.js'
import { FaTasks } from 'react-icons/fa'

function App() {
  const [players, setPlayers] = useState([
    {
        id: 1,
        name: 'Stephen Curry',
        year: '2020-21',
        viewStats: false,
    },

    {
        id: 2,
        name: 'Joel Embiid',
        year: '2020-21',
        viewStats: false,
    },

    {
        id: 3,
        name: 'Julius Randle',
        year: '2020-21',
        viewStats: false,
    },

    {
        id: 4,
        name: 'Immanuel Quickley',
        year: '2020-21',
        viewStats: false,
    },

    {
        id: 5,
        name: 'Andrew Wiggins',
        year: '2020-21',
        viewStats: false,
    },
])

// Delete Player
const deletePlayer = (id) => {
  setPlayers(players.filter((player) =>  player.id !== id))
}

// Toggle Player Status
const togglePlayer = (id) => {
  setPlayers(players.map((player) => player.id === id ? 
  {...player, viewStats: !player.viewStats} : player))
}

  return (
    <div className="container">
      <Header />
      <AddPlayer />
      {players.length > 0 ? (<Players nbaPlayers={players}
      onDelete={deletePlayer} onToggle = {togglePlayer} />) 
        : ('Add Players Please')} 
    </div>
  );
}

export default App;
